import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

server/app.ts.content:
import { type Server } from "node:http";
import { spawn } from "child_process";
import { join } from "path";

import express, {
  type Express,
  type Request,
  Response,
  NextFunction,
} from "express";

import { registerRoutes } from "./routes";
import { logger } from "./logger";

export function log(message: string, source = "express") {
  logger.info(message, source);
}

export const app = express();

declare module 'http' {
  interface IncomingMessage {
    rawBody: unknown
  }
}
app.use(express.json({
  verify: (req, _res, buf) => {
    req.rawBody = buf;
  }
}));
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let extra = "";
      if (capturedJsonResponse) {
        let resp = JSON.stringify(capturedJsonResponse);
        if (resp.length > 100) {
          resp = resp.slice(0, 99) + "…";
        }
        extra = resp;
      }

      logger.api(req.method, path, res.statusCode, duration, extra);
    }
  });

  next();
});

// Launch standalone ping.js process with auto-restart
function startPingProcess(port: number) {
  const pingScriptPath = join(process.cwd(), "ping.js");

  function launchPing() {
    try {
      const pingProcess = spawn("node", [pingScriptPath], {
        stdio: "inherit",
        detached: false,
        env: { ...process.env, PORT: port.toString() }
      });

      pingProcess.on("error", (err) => {
        logger.error(`Ping process error: ${err.message}`, "server", err);
        // Restart in 5 seconds
        setTimeout(launchPing, 5000);
      });

      pingProcess.on("exit", (code) => {
        logger.warn(`Ping process exited with code ${code}, restarting...`, "server");
        // Restart in 5 seconds
        setTimeout(launchPing, 5000);
      });

      logger.info(`✓ Ping process started (PID: ${pingProcess.pid})`, "server");
    } catch (err: any) {
      logger.error(`Failed to start ping process: ${err.message}`, "server", err);
      // Retry in 5 seconds
      setTimeout(launchPing, 5000);
    }
  }

  launchPing();
}

export default async function runApp(
  setup: (app: Express, server: Server) => Promise<void>,
) {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    logger.error(`HTTP ${status}: ${message}`, "express", err);
    res.status(status).json({ message });
  });

  // importantly run the final setup after setting up all the other routes so
  // the catch-all route doesn't interfere with the other routes
  await setup(app, server);

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  const env = process.env.NODE_ENV || "development";

  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port} (${env})`);
    log("Database connected", "db");
    log("WebSocket server ready at /ws", "ws");

    // Start standalone ping.js process to keep app alive on free tier
    // This helps prevent Replit free tier (15 min inactivity timeout)
    startPingProcess(port);
  });

  // Keep-alive: Prevent server timeout on free tier hosting
  server.keepAliveTimeout = 65000;
  server.headersTimeout = 66000;

  // Graceful shutdown on signals
  process.on("SIGTERM", () => {
    logger.warn("SIGTERM received, shutting down gracefully", "server");
    server.close(() => {
      logger.info("Server closed", "server");
      process.exit(0);
    });
  });

  process.on("SIGINT", () => {
    logger.warn("SIGINT received, shutting down gracefully", "server");
    server.close(() => {
      logger.info("Server closed", "server");
      process.exit(0);
    });
  });
}

client/src/components/BulkImportDialog.t.content:
import { useState } from "react";
import { useMutation } from "@tanstack/react-query";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface BulkImportDialogProps {
  packId: string;
  open: boolean;
  onOpenChange: (open: boolean) => void;
}

export function BulkImportDialog({ packId, open, onOpenChange }: BulkImportDialogProps) {
  const { toast } = useToast();
  const [text, setText] = useState("");

  const importMutation = useMutation({
    mutationFn: async (content: string) => {
      const lines = content.split("\n").filter(line => line.trim());
      const flashcards: Array<{ question: string; answer: string }> = [];

      for (const line of lines) {
        const match = line.match(/\("(.+?)",\s*"(.+?)"\)/);
        if (match) {
          flashcards.push({
            question: match[1],
            answer: match[2],
          });
        }
      }

      if (flashcards.length === 0) {
        throw new Error("Aucune carte trouvée. Vérifiez le format.");
      }

      await Promise.all(
        flashcards.map((card) =>
          apiRequest("POST", `/api/packs/${packId}/flashcards`, card)
        )
      );

      return flashcards.length;
    },
    onSuccess: (count) => {
      queryClient.invalidateQueries({ queryKey: ["/api/packs", packId, "flashcards"] });
      toast({
        title: "Cartes importées",
        description: `${count} carte(s) ont été ajoutées avec succès.`,
      });
      onOpenChange(false);
      setText("");
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Erreur",
        description: error.message || "Impossible d'importer les cartes.",
      });
    },
  });

  const handleImport = () => {
    if (!text.trim()) {
      toast({
        variant: "destructive",
        title: "Erreur",
        description: "Veuillez coller du contenu à importer.",
      });
      return;
    }
    importMutation.mutate(text);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle>Importer plusieurs cartes</DialogTitle>
          <DialogDescription>
            Collez vos cartes au format: ("Question", "Réponse")<br/>
            Une paire par ligne.
          </DialogDescription>
        </DialogHeader>
        <div className="space-y-4">
          <Textarea
            placeholder={`("Comment...?", "Réponse...")\n("Comment...?", "Réponse...")`}
            value={text}
            onChange={(e) => setText(e.target.value)}
            className="min-h-48 font-mono text-sm"
          />
          <div className="flex gap-2 justify-end">
            <Button variant="outline" onClick={() => onOpenChange(false)}>
              Annuler
            </Button>
            <Button
              className="gradient-violet-accent text-white border-0"
              onClick={handleImport}
              disabled={importMutation.isPending}
            >
              {importMutation.isPending ? "Import..." : "Importer"}
            </Button>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/pages/Home.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { BookOpen, GraduationCap, Layers } from "lucide-react";
import type { Pack, Flashcard } from "@shared/schema";
import { useAuth } from "@/lib/auth-context";
import { SUBJECTS } from "@shared/schema";

export default function Home() {
  const { user } = useAuth();

  const { data: packs, isLoading } = useQuery<Pack[]>({
    queryKey: ["/api/packs"],
  });

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 py-8">
        <div className="mb-8">
          <Skeleton className="h-10 w-64 mb-2" />
          <Skeleton className="h-6 w-96" />
        </div>
        <div className="grid grid-cols-1 gap-6">
          {[1, 2, 3].map((i) => (
            <Skeleton key={i} className="h-96" />
          ))}
        </div>
      </div>
    );
  }

  const publishedPacks = packs?.filter(pack => pack.published) || [];

  // Group packs by subject
  const packsGroupedBySubject = SUBJECTS.reduce((acc, subject) => {
    acc[subject] = publishedPacks.filter(p => p.subject === subject);
    return acc;
  }, {} as Record<string, Pack[]>);

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2 bg-gradient-to-r from-violet-600 to-purple-600 dark:from-violet-400 dark:to-purple-400 bg-clip-text text-transparent">
          Packs de Flashcard
        </h1>
        {user && (
          <p className="text-muted-foreground">
            Bienvenue, {user.firstName}!
          </p>
        )}
      </div>

      {publishedPacks.length === 0 ? (
        <Card className="text-center py-12">
          <CardContent className="space-y-4">
            <div className="flex justify-center">
              <div className="bg-muted p-4 rounded-full">
                <BookOpen className="h-12 w-12 text-muted-foreground" />
              </div>
            </div>
            <div>
              <h3 className="text-xl font-semibold mb-2">Aucun pack disponible</h3>
              <p className="text-muted-foreground">
                Il n'y a pas de packs de flashcard publiés pour le moment. Revenez bientôt!
              </p>
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-8">
          {SUBJECTS.map(subject => {
            const subjectPacks = packsGroupedBySubject[subject] || [];
            if (subjectPacks.length === 0) return null;

            return (
              <div key={subject}>
                <div className="mb-4">
                  <h2 className="text-2xl font-semibold text-violet-600 dark:text-violet-400 mb-1">
                    {subject}
                  </h2>
                  <p className="text-sm text-muted-foreground">
                    {subjectPacks.length} pack{subjectPacks.length !== 1 ? "s" : ""}
                  </p>
                </div>
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
                  {subjectPacks.map((pack) => (
                    <PackCard key={pack.id} pack={pack} />
                  ))}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

function PackCard({ pack }: { pack: Pack }) {
  const { data: flashcards, isLoading } = useQuery<Flashcard[]>({
    queryKey: ["/api/packs", pack.id, "flashcards"],
  });

  const cardCount = flashcards?.length || 0;

  return (
    <Card
      className="gradient-card min-h-80 flex flex-col hover-elevate active-elevate-2 transition-all cursor-pointer border-violet-200 dark:border-violet-800 shadow-md hover:shadow-lg"
      onClick={() => window.location.href = `/pack/${pack.id}`}
      data-testid={`card-pack-${pack.id}`}
    >
      <CardHeader className="flex-1">
        <div className="flex items-start gap-3">
          <div className="gradient-violet-accent p-2 rounded-md flex-shrink-0">
            <GraduationCap className="h-5 w-5 text-white" />
          </div>
          <div className="flex-1 min-w-0">
            <div className="flex items-start gap-2 mb-1">
              <CardTitle className="text-lg break-words" data-testid={`text-pack-title-${pack.id}`}>
                {pack.title}
              </CardTitle>
              <Badge variant="outline" className="flex-shrink-0" data-testid={`badge-pack-subject-${pack.id}`}>
                {pack.subject}
              </Badge>
            </div>
            <CardDescription className="line-clamp-2" data-testid={`text-pack-description-${pack.id}`}>
              {pack.description || "Pas de description"}
            </CardDescription>
          </div>
        </div>
      </CardHeader>
      <CardContent className="space-y-4">
        <div className="flex items-center gap-2">
          <Layers className="h-4 w-4 text-muted-foreground" />
          <span className="text-sm text-muted-foreground">
            {isLoading ? "..." : `${cardCount} carte${cardCount !== 1 ? "s" : ""}`}
          </span>
        </div>
        <Button variant="outline" className="w-full pointer-events-none" data-testid={`button-study-${pack.id}`}>
          Commencer à étudier
        </Button>
      </CardContent>
    </Card>
  );
}

