import { useLocation } from "wouter";
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { signupSchema, type SignupData } from "@shared/schema";
import { GraduationCap, Eye, EyeOff, ArrowLeft } from "lucide-react";

export default function Signup() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirm, setShowConfirm] = useState(false);

  const form = useForm<SignupData>({
    resolver: zodResolver(signupSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      password: "",
      confirmPassword: "",
    },
  });

  const signupMutation = useMutation({
    mutationFn: async (data: SignupData) => {
      return await apiRequest<{ status: string }>(
        "POST",
        "/api/signup",
        {
          firstName: data.firstName,
          lastName: data.lastName,
          password: data.password,
        }
      );
    },
    onSuccess: () => {
      toast({
        title: "Compte créé avec succès",
        description: "Votre demande a été envoyée à l'administrateur. Veuillez attendre son approbation.",
      });
      setLocation("/login");
    },
    onError: (error: Error) => {
      toast({
        variant: "destructive",
        title: "Erreur d'inscription",
        description: error.message || "Impossible de créer le compte. Veuillez réessayer.",
      });
    },
  });

  const onSubmit = (data: SignupData) => {
    signupMutation.mutate(data);
  };

  return (
    <div className="min-h-screen flex items-start justify-center bg-background p-4 pt-20">
      <Card className="w-full max-w-md">
        <CardHeader className="text-center space-y-2">
          <div className="flex justify-center mb-2">
            <div className="gradient-violet-accent p-3 rounded-lg">
              <GraduationCap className="h-8 w-8 text-white" />
            </div>
          </div>
          <CardTitle className="text-2xl">FlashCamEdu</CardTitle>
          <CardDescription>
            Créez votre compte pour commencer à apprendre
          </CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="firstName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Prénom</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Entrez votre prénom"
                        data-testid="input-signup-firstName"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="lastName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Nom</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Entrez votre nom"
                        data-testid="input-signup-lastName"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="password"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Mot de passe</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          type={showPassword ? "text" : "password"}
                          placeholder="Entrez votre mot de passe"
                          data-testid="input-signup-password"
                          {...field}
                        />
                        <button
                          type="button"
                          onClick={() => setShowPassword(!showPassword)}
                          className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
                          data-testid="button-toggle-password"
                        >
                          {showPassword ? (
                            <EyeOff className="h-4 w-4" />
                          ) : (
                            <Eye className="h-4 w-4" />
                          )}
                        </button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="confirmPassword"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Confirmer le mot de passe</FormLabel>
                    <FormControl>
                      <div className="relative">
                        <Input
                          type={showConfirm ? "text" : "password"}
                          placeholder="Confirmer votre mot de passe"
                          data-testid="input-signup-confirmPassword"
                          {...field}
                        />
                        <button
                          type="button"
                          onClick={() => setShowConfirm(!showConfirm)}
                          className="absolute right-3 top-1/2 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
                          data-testid="button-toggle-confirmPassword"
                        >
                          {showConfirm ? (
                            <EyeOff className="h-4 w-4" />
                          ) : (
                            <Eye className="h-4 w-4" />
                          )}
                        </button>
                      </div>
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <Button
                type="submit"
                className="gradient-violet-accent text-white border-0 w-full"
                disabled={signupMutation.isPending}
                data-testid="button-submit-signup"
              >
                {signupMutation.isPending ? "Création..." : "Créer un compte"}
              </Button>
            </form>
          </Form>
          <div className="mt-6 pt-6 border-t">
            <Button
              variant="ghost"
              className="w-full"
              onClick={() => setLocation("/login")}
              data-testid="button-back-to-login"
            >
              <ArrowLeft className="h-4 w-4 mr-2" />
              Retour à la connexion
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/Messages.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useAuth } from "@/lib/auth-context";
import { useNotifications } from "@/lib/notification-context";
import { insertMessageSchema } from "@shared/schema";
import type { User, Message } from "@shared/schema";
import { Send, MessageSquare, Search } from "lucide-react";
import { z } from "zod";

const sendMessageSchema = insertMessageSchema.extend({
  toUserId: z.string().min(1, "Recipient is required"),
});

export default function Messages() {
  const { user: currentUser } = useAuth();
  const { setUnreadCount } = useNotifications();
  const { toast } = useToast();
  const [selectedUserId, setSelectedUserId] = useState<string>("");
  const [searchTerm, setSearchTerm] = useState<string>("");
  const [unreadByUser, setUnreadByUser] = useState<Record<string, boolean>>({});
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const { data: recipients = [] } = useQuery<User[]>({
    queryKey: ["/api/messages/recipients"],
    refetchInterval: 2000,
  });

  const { data: allMessages = [] } = useQuery<Message[]>({
    queryKey: ["/api/messages"],
    refetchInterval: 1000,
  });

  const { data: unreadCount = 0 } = useQuery<number>({
    queryKey: ["/api/notifications/unread-count"],
    refetchInterval: 2000,
  });

  const form = useForm({
    resolver: zodResolver(sendMessageSchema),
    defaultValues: {
      toUserId: "",
      content: "",
      fromUserId: "",
    },
  });

  const sendMutation = useMutation({
    mutationFn: (data: z.infer<typeof sendMessageSchema>) =>
      apiRequest("POST", "/api/messages", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/messages"] });
      form.reset({ toUserId: selectedUserId, content: "", fromUserId: "" });
    },
    onError: () => {
      toast({
        variant: "destructive",
        title: "Erreur",
        description: "Impossible d'envoyer le message.",
      });
    },
  });

  const markConversationReadMutation = useMutation({
    mutationFn: (otherUserId: string) =>
      apiRequest("POST", "/api/notifications/mark-conversation-read", { otherUserId }),
    onSuccess: (data: any) => {
      setUnreadByUser(prev => ({ ...prev, [selectedUserId]: false }));
      setUnreadCount(data.unreadCount);
      queryClient.invalidateQueries({ queryKey: ["/api/messages"] });
      queryClient.invalidateQueries({ queryKey: ["/api/notifications/unread-count"] });
    },
  });

  const onSubmit = (data: z.infer<typeof sendMessageSchema>) => {
    if (!data.toUserId) {
      toast({
        variant: "destructive",
        title: "Erreur",
        description: "Veuillez sélectionner un destinataire.",
      });
      return;
    }
    sendMutation.mutate(data);
  };

  const handleKeyDown = (e: React.KeyboardEvent<HTMLTextAreaElement>) => {
    if (e.key === "Enter" && !e.shiftKey) {
      e.preventDefault();
      form.handleSubmit(onSubmit)();
    }
  };

  const selectedUser = recipients.find(u => u.id === selectedUserId);

  const conversationMessages = allMessages
    .filter(m => (m.fromUserId === selectedUserId && m.toUserId === currentUser?.id) || 
                  (m.fromUserId === currentUser?.id && m.toUserId === selectedUserId))
    .sort((a, b) => new Date(a.createdAt).getTime() - new Date(b.createdAt).getTime());

  const filteredRecipients = recipients.filter(user =>
    `${user.firstName} ${user.lastName}`.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const getRoleLabel = (role: string) => {
    return role === "admin" ? "Administrateur" : role === "teacher" ? "Professeur" : "Étudiant";
  };

  const getUnreadForUser = (userId: string): number => {
    return allMessages.filter(m =>
      m.fromUserId === userId && m.toUserId === currentUser?.id && !m.read
    ).length;
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [conversationMessages]);

  useEffect(() => {
    if (selectedUserId && getUnreadForUser(selectedUserId) > 0) {
      markConversationReadMutation.mutate(selectedUserId);
    }
  }, [selectedUserId]);

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">Messages</h1>
        <p className="text-muted-foreground">Communiquez en temps réel avec vos collègues</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 h-[70vh]">
        {/* Contacts */}
        <div className="lg:col-span-1 flex flex-col">
          <Card className="flex-1 flex flex-col overflow-hidden">
            <CardHeader>
              <CardTitle className="text-lg">Contacts</CardTitle>
              <CardDescription>Sélectionnez un contact</CardDescription>
            </CardHeader>
            <CardContent className="flex-1 flex flex-col gap-4 overflow-hidden">
              <div className="relative">
                <Search className="absolute left-2 top-1/2 -translate-y-1/2 h-4 w-4 text-muted-foreground" />
                <Input
                  placeholder="Chercher..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-8 text-sm"
                  data-testid="input-search-recipients"
                />
              </div>

              <div className="space-y-2 overflow-y-auto flex-1">
                {filteredRecipients.length === 0 ? (
                  <p className="text-sm text-muted-foreground text-center py-4">
                    {recipients.length === 0 ? "Aucun contact" : "Pas de résultats"}
                  </p>
                ) : (
                  filteredRecipients.map(contact => {
                    const unread = getUnreadForUser(contact.id);
                    return (
                      <button
                        key={contact.id}
                        onClick={() => {
                          setSelectedUserId(contact.id);
                          form.setValue("toUserId", contact.id);
                        }}
                        className={`w-full text-left p-2 rounded-md transition-colors border text-sm relative ${
                          selectedUserId === contact.id
                            ? "bg-violet-100 dark:bg-violet-900 border-violet-300 dark:border-violet-700"
                            : "border-transparent hover:bg-muted"
                        }`}
                        data-testid={`button-select-user-${contact.id}`}
                      >
                        <div className="font-medium truncate">{contact.firstName} {contact.lastName}</div>
                        <div className="text-xs text-muted-foreground flex justify-between">
                          <span>{getRoleLabel(contact.role)}</span>
                          {unread > 0 && (
                            <span className="inline-flex items-center gap-1">
                              <div className="w-1.5 h-1.5 bg-red-500 rounded-full" />
                              {unread}
                            </span>
                          )}
                        </div>
                      </button>
                    );
                  })
                )}
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Chat */}
        <div className="lg:col-span-2 flex flex-col gap-4">
          {selectedUser ? (
            <>
              {/* Header */}
              <Card className="flex-shrink-0">
                <CardHeader className="py-3">
                  <div className="flex items-center justify-between">
                    <div>
                      <CardTitle className="text-base">{selectedUser.firstName} {selectedUser.lastName}</CardTitle>
                      <CardDescription className="text-xs">{getRoleLabel(selectedUser.role)}</CardDescription>
                    </div>
                    <Badge>{getRoleLabel(selectedUser.role)}</Badge>
                  </div>
                </CardHeader>
              </Card>

              {/* Messages */}
              <Card className="flex-1 flex flex-col overflow-hidden">
                <CardContent className="flex-1 overflow-y-auto p-4 space-y-3 max-h-96">
                  {conversationMessages.length === 0 ? (
                    <div className="h-full flex items-center justify-center">
                      <div className="text-center">
                        <MessageSquare className="h-12 w-12 mx-auto mb-2 text-muted-foreground opacity-30" />
                        <p className="text-sm text-muted-foreground">Aucun message</p>
                      </div>
                    </div>
                  ) : (
                    <>
                      {conversationMessages.map(msg => (
                        <div
                          key={msg.id}
                          className={`flex gap-2 ${msg.fromUserId === currentUser?.id ? "justify-end" : "justify-start"}`}
                        >
                          <div
                            className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg text-sm ${
                              msg.fromUserId === currentUser?.id
                                ? "bg-violet-500 text-white rounded-br-none"
                                : "bg-muted text-foreground rounded-bl-none"
                            }`}
                            data-testid={`message-${msg.id}`}
                          >
                            <p className="break-words">{msg.content}</p>
                            <div className={`text-xs mt-1 ${
                              msg.fromUserId === currentUser?.id
                                ? "text-violet-100"
                                : "text-muted-foreground"
                            }`}>
                              {new Date(msg.createdAt).toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" })}
                            </div>
                          </div>
                        </div>
                      ))}
                      <div ref={messagesEndRef} />
                    </>
                  )}
                </CardContent>
              </Card>

              {/* Form */}
              <Card className="flex-shrink-0">
                <CardContent className="pt-6">
                  <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-3">
                      <FormField
                        control={form.control}
                        name="content"
                        render={({ field }) => (
                          <FormItem>
                            <FormControl>
                              <Textarea
                                placeholder="Écrivez votre message..."
                                className="resize-none min-h-16"
                                data-testid="input-message-content"
                                onKeyDown={handleKeyDown}
                                {...field}
                              />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <Button
                        type="submit"
                        disabled={sendMutation.isPending}
                        className="w-full gradient-violet-accent text-white border-0"
                        size="sm"
                        data-testid="button-send-message"
                      >
                        <Send className="h-4 w-4 mr-2" />
                        Envoyer
                      </Button>
                    </form>
                  </Form>
                </CardContent>
              </Card>
            </>
          ) : (
            <Card className="flex-1 flex items-center justify-center">
              <CardContent className="text-center">
                <MessageSquare className="h-16 w-16 mx-auto mb-4 text-muted-foreground opacity-30" />
                <h3 className="font-semibold mb-2">Pas de conversation sélectionnée</h3>
                <p className="text-sm text-muted-foreground">Sélectionnez un contact pour démarrer</p>
              </CardContent>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

client/src/pages/AdminFlashcards.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useToast } from "@/hooks/use-toast";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { insertPackSchema, SUBJECTS } from "@shared/schema";
import type { Pack, User } from "@shared/schema";
import { Plus, Edit2, Trash2, BookOpen, ChevronDown, ChevronUp } from "lucide-react";
import { z } from "zod";

const createPackSchema = insertPackSchema;

interface PackWithCreator extends Pack {
  creator?: User | null;
}

export default function AdminFlashcards() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [isCreating, setIsCreating] = useState(false);
  const [expandedSections, setExpandedSections] = useState<Set<string>>(new Set());

  const { data: packs = [] } = useQuery<PackWithCreator[]>({
    queryKey: ["/api/packs"],
    refetchInterval: 2000,
  });

  const { data: allUsers = [] } = useQuery<User[]>({
    queryKey: ["/api/admin/users"],
  });

  const form = useForm({
    resolver: zodResolver(createPackSchema),
    defaultValues: {
      title: "",
      description: "",
      subject: SUBJECTS[0],
    },
  });

  const createMutation = useMutation({
    mutationFn: (data: z.infer<typeof createPackSchema>) =>
      apiRequest("POST", "/api/packs", data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/packs"] });
      form.reset({ subject: SUBJECTS[0] });
      setIsCreating(false);
      toast({ title: "Succès", description: "Pack créé" });
    },
    onError: (error: Error) => {
      toast({ variant: "destructive", title: "Erreur", description: error.message });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: (id: string) => apiRequest("DELETE", `/api/packs/${id}`, {}),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/packs"] });
      toast({ title: "Succès", description: "Pack supprimé" });
    },
  });

  // Create a map of userId to user
  const userMap = allUsers.reduce((acc, user) => {
    acc[user.id] = user;
    return acc;
  }, {} as Record<string, User>);

  // Enhance packs with creator info
  const enhancedPacks = packs.map(pack => ({
    ...pack,
    creator: pack.createdByUserId ? userMap[pack.createdByUserId] : null,
  }));

  // Group packs by subject, then separate "Autres Packs de cours" (no creator)
  const packsGroupedBySubject = SUBJECTS.reduce((acc, subject) => {
    const subjectPacks = enhancedPacks.filter(p => p.subject === subject);
    const withCreator = subjectPacks.filter(p => p.creator);
    acc[subject] = withCreator;
    return acc;
  }, {} as Record<string, PackWithCreator[]>);

  const otherPacks = enhancedPacks.filter(p => !p.creator);

  const toggleSection = (section: string) => {
    const newExpanded = new Set(expandedSections);
    if (newExpanded.has(section)) {
      newExpanded.delete(section);
    } else {
      newExpanded.add(section);
    }
    setExpandedSections(newExpanded);
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">Flashcards (Administrateur)</h1>
        <p className="text-muted-foreground">Gérez tous les packs de toutes les matières</p>
      </div>

      {isCreating && (
        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Créer un nouveau pack</CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit((data) => createMutation.mutate(data))} className="space-y-4">
                <FormField
                  control={form.control}
                  name="title"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Titre</FormLabel>
                      <FormControl>
                        <Input placeholder="Titre du pack" {...field} data-testid="input-pack-title" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea placeholder="Description du pack" {...field} data-testid="input-pack-description" />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="subject"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Matière</FormLabel>
                      <Select value={field.value} onValueChange={field.onChange}>
                        <FormControl>
                          <SelectTrigger data-testid="select-subject">
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {SUBJECTS.map(subject => (
                            <SelectItem key={subject} value={subject}>{subject}</SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <div className="flex gap-2">
                  <Button type="submit" disabled={createMutation.isPending} data-testid="button-create-pack">
                    Créer
                  </Button>
                  <Button type="button" variant="outline" onClick={() => setIsCreating(false)}>
                    Annuler
                  </Button>
                </div>
              </form>
            </Form>
          </CardContent>
        </Card>
      )}

      {!isCreating && (
        <Button className="mb-6" onClick={() => setIsCreating(true)} data-testid="button-new-pack">
          <Plus className="h-4 w-4 mr-2" />
          Nouveau pack
        </Button>
      )}

      {packs.length === 0 ? (
        <Card className="text-center py-12">
          <CardContent className="space-y-4">
            <BookOpen className="h-12 w-12 mx-auto text-muted-foreground opacity-50" />
            <div>
              <h3 className="text-xl font-semibold mb-2">Aucun pack</h3>
              <p className="text-muted-foreground">Créez votre premier pack de flashcards</p>
            </div>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-6">
          {/* Packs with creators grouped by subject */}
          {SUBJECTS.map(subject => {
            const subjectPacks = packsGroupedBySubject[subject] || [];
            if (subjectPacks.length === 0) return null;

            const isExpanded = expandedSections.has(subject);

            return (
              <div key={subject}>
                <button
                  onClick={() => toggleSection(subject)}
                  className="w-full flex items-center gap-2 p-3 rounded-md bg-gradient-to-r from-violet-100 to-purple-100 dark:from-violet-900 dark:to-purple-900 hover-elevate transition-colors text-left"
                  data-testid={`button-toggle-subject-${subject}`}
                >
                  {isExpanded ? <ChevronUp className="h-5 w-5" /> : <ChevronDown className="h-5 w-5" />}
                  <span className="font-semibold text-lg">{subject}</span>
                  <span className="text-sm text-muted-foreground ml-auto">({subjectPacks.length})</span>
                </button>

                {isExpanded && (
                  <div className="mt-3 space-y-3 pl-4 border-l-2 border-violet-300 dark:border-violet-700">
                    {subjectPacks.map(pack => (
                      <Card key={pack.id} className="hover-elevate">
                        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-3 p-4">
                          <div className="flex-1">
                            <CardTitle className="text-base">{pack.title}</CardTitle>
                            <CardDescription className="text-sm">{pack.description}</CardDescription>
                            {pack.creator && (
                              <p className="text-xs text-muted-foreground mt-1">
                                Par: {pack.creator.firstName} {pack.creator.lastName}
                              </p>
                            )}
                          </div>
                          <div className="flex gap-2">
                            <Button
                              size="sm"
                              variant="outline"
                              onClick={() => setLocation(`/pack/${pack.id}`)}
                              data-testid={`button-edit-pack-${pack.id}`}
                            >
                              <Edit2 className="h-4 w-4" />
                            </Button>
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={() => deleteMutation.mutate(pack.id)}
                              disabled={deleteMutation.isPending}
                              data-testid={`button-delete-pack-${pack.id}`}
                            >
                              <Trash2 className="h-4 w-4" />
                            </Button>
                          </div>
                        </CardHeader>
                      </Card>
                    ))}
                  </div>
                )}
              </div>
            );
          })}

          {/* Other packs (no creator) */}
          {otherPacks.length > 0 && (
            <div>
              <button
                onClick={() => toggleSection("autres")}
                className="w-full flex items-center gap-2 p-3 rounded-md bg-gray-100 dark:bg-gray-800 hover-elevate transition-colors text-left"
                data-testid="button-toggle-autres"
              >
                {expandedSections.has("autres") ? <ChevronUp className="h-5 w-5" /> : <ChevronDown className="h-5 w-5" />}
                <span className="font-semibold text-lg">Autres Packs de cours</span>
                <span className="text-sm text-muted-foreground ml-auto">({otherPacks.length})</span>
              </button>

              {expandedSections.has("autres") && (
                <div className="mt-3 space-y-3 pl-4 border-l-2 border-gray-300 dark:border-gray-700">
                  {otherPacks.map(pack => (
                    <Card key={pack.id} className="hover-elevate">
                      <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-3 p-4">
                        <div className="flex-1">
                          <CardTitle className="text-base">{pack.title}</CardTitle>
                          <CardDescription className="text-sm">{pack.description}</CardDescription>
                          <p className="text-xs text-muted-foreground mt-1">
                            Matière: {pack.subject}
                          </p>
                        </div>
                        <div className="flex gap-2">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => setLocation(`/pack/${pack.id}`)}
                            data-testid={`button-edit-pack-${pack.id}`}
                          >
                            <Edit2 className="h-4 w-4" />
                          </Button>
                          <Button
                            size="sm"
                            variant="destructive"
                            onClick={() => deleteMutation.mutate(pack.id)}
                            disabled={deleteMutation.isPending}
                            data-testid={`button-delete-pack-${pack.id}`}
                          >
                            <Trash2 className="h-4 w-4" />
                          </Button>
                        </div>
                      </CardHeader>
                    </Card>
                  ))}
                </div>
              )}
            </div>
          )}
        </div>
      )}
    </div>
  );
}

